package com.seluhadu.shchat.adapters;import androidx.annotation.NonNull;import androidx.fragment.app.Fragment;import androidx.fragment.app.FragmentManager;import androidx.fragment.app.FragmentPagerAdapter;import java.util.ArrayList;import java.util.List;public class ChatViewPagerAdapter extends FragmentPagerAdapter {    private List<Fragment> mListFragment = new ArrayList<>();    public ChatViewPagerAdapter(FragmentManager fm) {        super(fm);    }    @NonNull    @Override    public Fragment getItem(int position) {        return mListFragment.get(position);    }    public void addFragment(Fragment fragment) {        mListFragment.add(fragment);    }    @Override    public int getCount() {        return mListFragment.size();    }}