package com.seluhadu.shchat.dialogs;import android.app.Dialog;import android.content.DialogInterface;import android.os.Bundle;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import com.google.android.material.bottomsheet.BottomSheetBehavior;import com.google.android.material.bottomsheet.BottomSheetDialog;import com.google.android.material.bottomsheet.BottomSheetDialogFragment;import androidx.recyclerview.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.FrameLayout;import android.widget.TextView;import com.seluhadu.shchat.R;import de.hdodenhof.circleimageview.CircleImageView;public class DialogComment extends BottomSheetDialogFragment {    public DialogComment() {    }    public static DialogComment newInstance() {        Bundle args = new Bundle();        DialogComment fragment = new DialogComment();        fragment.setArguments(args);        return fragment;    }    @Nullable    @Override    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        View rootView = inflater.inflate(R.layout.dialog_comment, container, false);        CircleImageView imageProfile = rootView.findViewById(R.id.user_profile);        TextView userName = rootView.findViewById(R.id.user_name);        TextView date_and_time = rootView.findViewById(R.id.date_and_time);        RecyclerView recyclerView = rootView.findViewById(R.id.comment_recycler_view);        return rootView;    }    @NonNull    @Override    public Dialog onCreateDialog(Bundle savedInstanceState) {        final BottomSheetDialog dialogFragment = (BottomSheetDialog) super.onCreateDialog(savedInstanceState);        dialogFragment.setOnShowListener(dialog -> {            BottomSheetDialog d = (BottomSheetDialog) dialog;            FrameLayout bottomSheet = d.findViewById(com.google.android.material.R.id.design_bottom_sheet);            assert bottomSheet != null;            BottomSheetBehavior.from(bottomSheet).setState(BottomSheetBehavior.STATE_EXPANDED);        });        return dialogFragment;    }}